import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { StoreBaseUrl } from '../../../../constants';

function ShopOwnerAccount() {
    const [profileData, setProfileData] = useState({})
    const getprofile = async () => {
        axios 
          .get(StoreBaseUrl + "user/profile", {
            headers: {
              Authorization: `Token ${localStorage.getItem("token")}`,
            },
          })
          .then((res) => {
            console.log(res.data);
            setProfileData(res.data);
            // dispatch(res.data)
            // dispatchEvent.setStores(res);
          })
          .catch((e) => {
            console.log(e);
          });
      };
    
      useEffect(()=>{getprofile()},[])
  return (
    <div className='m-4'>
      <p className='text-2xl font-bold'>hello {profileData.username}</p>
      <p className='text-xs'>Here you can update your profile informations like profile image, contat number, email and all the account informations of yours.</p><div className="pl-96 pt-24 ">
           
            <div class="">
              <div class="">
                <div class="flex">
                  <label
                    class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                    for="grid-first-name"
                  >
                    Store Name
                  </label>
                  <input
                    class="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                    id="grid-first-name"
                    type="text"
                    name="store_name"
                    placeholder="store name"
                    // value={store_name}
                    // onChange={(e) => setStoreFormData({...storeFormData, store_name:e.target.value})}
                    />
                  <p class="text-red-500 text-xs italic">
                    Please fill out this field.
                  </p>
                </div>
                <div class="flex">
                  <label
                    class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                    for="grid-last-name"
                  >
                    License Number
                  </label>
                  <input
                    class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                    id="grid-last-name"
                    type="text"
                    name="store_license_number"
                    placeholder="licence number"
                    // value={store_licence_number}
                    // onChange={(e) =>  setStoreFormData({...storeFormData, store_licence_number:e.target.value})}

                  />
                </div>{" "}
                <div class="flex">
                  <label
                    class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                    for="grid-last-name"
                  >
                    Store Image Url
                  </label>
                  <input
                    class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                    id="grid-last-name"
                    type="text"
                    name="store_image_url"
                    placeholder="url"
                    // value={store_image_ur}
                    // onChange={(e)=>{ setStoreFormData({...storeFormData, store_image_url:e.target.value})}}
                  />
                </div>{" "}
                
                <div class="">
                  <label
                    class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                    for="grid-last-name"
                  >
                    Open Dates
                  </label>
                  <input
                    class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                    id="grid-last-name"
                    type="text"
                    placeholder="dates"
                    name="store_dates"
                    // value={store_open_dates}
                    // onChange={(e)=>{ setStoreFormData({...storeFormData, store_open_dates:e.target.value})}}
                  />
                </div>
              </div>
              <div class="">
               
                  <label
                    class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                    for="grid-password"
                  >
                    Store Description
                  </label>
                  <input
                    class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                    id="grid-password"
                    type="text"
                    placeholder="Type about your store...."
                    name="store_description"
                    // value={store_description}
                    // onChange={(e)=>{ setStoreFormData({...storeFormData, store_description:e.target.value})}}
                  />
                  <p class="text-gray-600 text-xs italic">
                    Make it as long and as crazy as you'd like
                  </p>
               
              </div>
             

              <div className="flex justify-end">
                {/* add store button */}
                <button
                  className="p-3 rounded-md bg-blue-500 text-white"
                //   onClick={submitAddStoreForm}
                >
                  Add store
                </button>
              </div>
            </div>
          </div>
    </div>
  )
}

export default ShopOwnerAccount
