import React from 'react'

function Login() {

  const showPassword = ()=>{

    const usernameInput = document.getElementById('usernameInput')
    const passwordInput = document.getElementById('passwordInput')

    usernameInput.style.display = "none"
    passwordInput.style.display = ""
  }
  const showUsername = ()=>{

    const inputFields = document.hasAttribute('inputType')
    // const passwordInput = document.getElementsByClassName('passwordInput')

    if(inputFields)
    usernameInput.style.display = "block"
    passwordInput.style.display = "none"
  }
  return (
    <div className='flex justify-center items-center h-screen'>
    <section className='border border-gray-700 p-20 rounded-md w-3/4 flex '>
      <section id='logo' className='w-1/2'>
        <img src='/menuit-logo.jpg' className='h-36'/>
      </section>
      <section id='inputs' className='w-1/2  '>
        {/* <h2>Login</h2> */}
       
        <div className='w-full usernameInput' inputType="usernameInput">
            <p className='font-semibold'>Username / Email ID</p>
            <p className='text-sm text-gray-600'>Enter your email id that you have used at the time of your registration</p>
            <input type='text' className='border mt-5 border-gray-600 p-2 w-full rounded-md'/>
          </div>

          <div className='w-full passwordInput'  >
            <p className='font-semibold'>Password</p>
            <p className='text-sm text-gray-600'>Enter your email id that you have used at the time of your registration</p>
            <input type='text' className='border mt-5 border-gray-600 p-2 w-full rounded-md'/>
          </div>
          
          
          <div className='usernameInput  ' >
            <div onClick={showPassword} className='p-2 mt-2 bg-green-700 text-white rounded-md'>Next</div>
          </div>

          <div className='flex justify-end passwordInput'>
            <div onClick={showUsername} className='p-2 mt-2 bg-green-700 text-white rounded-md'>Previous</div>
          </div>
       
      </section>
 
    </section>
      
    </div>
  )
}

export default Login
