import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useNavigate, useRevalidator } from 'react-router-dom'
import { StoreBaseUrl } from '../constants'

function ShopList() {

    const navigate = useNavigate()
    

    const gotoStore=(id)=>{
        localStorage.setItem('store_id',id)
        navigate('store')
    }

    const [shopData, setShopData] = useState([])

    const getStores = ()=>{
        axios.get(StoreBaseUrl + '/stores/all', {
            'headers':`Token ${token}`
        }).then((res)=>{
            setShopData(res.data)
        }).catch((e)=>{
            console.log(e)
        })
    }

    useEffect(()=>{
        getStores()
    },[])
  return (
    <div className=''>
        {shopData.map(shop=>{
            return (
                <div  onClick={()=>gotoStore(shop.id)} className='border border-gray-500 p-3 m-2 rounded-md cursor-pointer hover:border-blue-500  flex'>
               <img src='shop_default.avif' className='w-28'/>
               <div>
               <p>{shop.shopName}</p>
                <div className='text-sm font-bold'>{shop.LicenseNumber}</div>
                <p className='text-sm text-gray-700'>{shop.description}</p>
                <p>Rating {shop.rating}</p>

                </div> 
                </div>
     
            )
        })}
    </div>
  )
}

export default ShopList
