import React, { useEffect, useState } from 'react'
import Aqua from '../../Themes/Aqua/Aqua'
import { Darker } from '../../Themes/Darker/Darker'
import axios from 'axios'

function StoreView() {
 const [theme, setTheme] = useState('aqua')
 const [products, setProducts] = useState([])

 const getProducts =async()=>{
   const res = await axios.get('https://4ka6rp5lp5.execute-api.ap-south-1.amazonaws.com/production/api/store/product?shop_owner_username=amal&division_name=main hall&store_name=malabar').then((res)=>{
      console.log(res.data)
      setProducts(res.data)
   }).catch((e)=>{
      setProducts([])
   })
  
}
const search =async(keyword)=>{
   if (keyword != null){
      const res = await axios.get(`https://4ka6rp5lp5.execute-api.ap-south-1.amazonaws.com/production/api/store/product?shop_owner_username=amal&division_name=main hall&store_name=malabar&search=${keyword}`).then((re))
      console.log("searching...")
      setProducts(res.data)
   }
   
  
}
 const data = {
    'store_name':'malabar',
    'contact':'9946658045',
    'license':'74237482394237497239',
    'products':products,
    'search':search
 }

 useEffect(()=>{
    getProducts()
 },[])

  return (
    <div>
        {theme === 'aqua'?<Aqua data = {data}/>:""}
        {theme === 'darker'?<Darker data={data}/>:""}
    </div>
  )
}

export default StoreView
