import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { StoreBaseUrl } from '../../constants'

const Aqua = (props) => {

  const [page, setPage] = useState('division_list')
  const [divisions, setDivisions] = useState([])
  const [products, setProducts] = useState([])

  
  const parameters = new URLSearchParams(window.location.search)
  const id= parameters.get('store_id')

  const search =async(keyword="",)=>{
    if (keyword != null){
       await axios.get(StoreBaseUrl + `store/product?store_id=${id}&search=${keyword}`).then((res)=>{
       }).catch((e)=>{
       })
    }
 }

 
 const Fdivision =async()=>{
  setPage('')

  console.log('sending request  ')
     await axios.get(StoreBaseUrl + `store/division?store_id=${id}`).then((res)=>{
      setDivisions(res.data)
      setPage('division_list')

     }).catch((e)=>{
     })
  
}

const setPage_to_productList =async (division_id)=>{
  setPage('')

  await axios.get(StoreBaseUrl + `store/product?store_id=${id}&division_name=${divi}`).then((res)=>{  
   console.log(res.data)
  setProducts(res.data)

   setPage('product_list')

  }).catch((e)=>{
  })
}
const setPage_to_productView =async ()=>{
  setPage('')

       await axios.get(StoreBaseUrl + `store/product?store_id=${id}&division_name=front`).then((res)=>{  
        console.log(res.data)
       setProducts(res.data)

        setPage('product_list')

       }).catch((e)=>{
       })
}


 useEffect(()=>{
  Fdivision()
 },[])

  return (
  <div className='overflow-y-hidden '>
     <div id='top_banner' style={{'backgroundImage':'url('+props.storeinfo.store_image_url+')'}} className='p-10  text-white w-screen '>
      <p className='text-4xl'>
      {props.storeinfo.store_name}
      <p className='text-sm'>Store Name</p>

      {/* <p>Contact Detail {props.storeinfo.store_license_number}</p> */}
      <p>{props.storeinfo.store_license_number}</p>
      <p className='text-sm'>License Number</p>
      </p>
      <p id='description'>
          {props.storeinfo.store_desription}
      </p>
    </div>
    {

      

    page == 'product_list'?
    <>
   

      <div className='flex p-10'>
      {products.map((product, index)=>{
        return (
            <div className='border border-gray-500 rounded p-9 cursor-pointer' onClick={setPage_to_productView}>
            <p>{product.product_name}</p>
            <p className='text-sm text-gray-600'>{product.product_description}</p>
            <p className='text-sm text-gray-600'>Stock {product.product_available}</p>
            {/* <p className='text-sm text-gray-600'>{product.product_store}</p> */}
            <p>Rs. {product.product_price}</p>
            <p>Rating {product.product_rating}</p>

          </div>
        )
      })}
    </div>
    </>

    :page == 'category_list'?
    <div>
    </div>
    
    :page == 'division_list'?
    <div className='flex p-10'>
      {divisions.map((division, index)=>{
        return (
            <div className='border border-gray-500 rounded p-9 cursor-pointer m-5' onClick={()=>setPage_to_productList(division.id)}>
            {division.division_name}

          </div>
        )
      })}
    </div>
    :
    <div className='flex text-black align-middle '>
      Loading
    </div>
    }

  </div>
  )
}

export default Aqua
