import React, { useEffect, useState } from 'react'
import Aqua from '../../Themes/Aqua/Aqua'
import { Darker } from '../../Themes/Darker/Darker'
import axios from 'axios'
import {StoreBaseUrl} from '../../constants'

function StoreView() {
 const [theme, setTheme] = useState('aqua')
 const [products, setProducts] = useState([])
 const [storeData, setStoreData] = useState({
   'store_name':'',
   'contact':'',
   'license':'',
   'products':[]
})

 const getProducts =async()=>{
      await axios.get('https://4ka6rp5lp5.execute-api.ap-south-1.amazonaws.com/production/api/store/product?shop_owner_username=amal&division_name=main hall&store_name=malabar').then((res)=>{
      console.log(res.data)
      setProducts(res.data)
   }).catch((e)=>{
      setProducts([])
   })
  
}
const search =async(keyword)=>{
   if (keyword != null){
      await axios.get(`https://4ka6rp5lp5.execute-api.ap-south-1.amazonaws.com/production/api/store/product?shop_owner_username=amal&division_name=main hall&store_name=malabar&search=${keyword}`).then((res)=>{

      console.log("searching...")
      setProducts(res.data)
      }).catch((e)=>{
         setProducts([])
      })
   }
   
  
}

const getStoreData = async()=>{
   const store_owner_username  = "amal"
   const division_name = "main hall"
   const store_name = "malabar"

  await axios.get(StoreBaseUrl + store_owner_username + store_name + division_name ).then((res)=>{
   setStoreData(res.data)
  })
   
}
 const data = {
    'store_name':'malabar',
    'contact':'9946658045',
    'license':'74237482394237497239',
    'products':products,
    'search':search
 }

 useEffect(()=>{
    getProducts()
 },[])

  return (
    <div>
        {theme === 'aqua'?<Aqua data = {storeData}/>:""}
        {theme === 'darker'?<Darker data={storeData}/>:""}
    </div>
  )
}

export default StoreView
